import React, { useState } from "react";

import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import Mic from "../Assets/mic.svg";
import OnMic from "../Assets/micon.svg";

const Dictaphone = ({ input, setinput }) => {
  const [mic, setMic] = useState(false);
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  if (!browserSupportsSpeechRecognition) {
    return alert("Browser doesn't support speech recognition");
  }
  var lang="en-US"
  const onMicStart = () => {
    setMic(!mic);
    if (mic) {
      console.log(lang)
      SpeechRecognition.startListening({
        language: lang, // Default to English
      });
    } else {
      SpeechRecognition.stopListening();
    }
    console.log(transcript);
    if(transcript==="Tamil"){
      console.log("hey tam");
      lang="ta-IN"
    }
    else{
      console.log("heyu eng");
      lang="en-US"
    }
    console.log(transcript);
    setinput(transcript);
  };

  // const onMicStart = () => {
  //   setMic(!mic);
  //   mic
  //     ? SpeechRecognition.startListening()
  //     : SpeechRecognition.stopListening();
  //   console.log(transcript);
  //   setinput(transcript);
  // };

  return (
    <div>
      <img
        src={listening ? OnMic : Mic}
        alt="Mic"
        className="w-[4.5vw] h-[5vh] mt-2 px-0 cursor-pointer transition-all user-select-none"
        onClick={onMicStart}
      />
    </div>
  );
};
export default Dictaphone;
